  let D := matrix3sumBottomLeft x₀ᵣ x₁ᵣ x₂ᵣ y₀ₗ y₁ₗ y₂ₗ Dₗ D₀ Dᵣ;
  let c₀ := Bᵣ._col y₀ᵣ;
